<?phpclass Curl{    private $host;    public $last_response = "";    public $last_http_code = null;    const METHOD_POST = "post";    const METHOD_GET = "get";    public function __construct($host = "")    {        $this->host = $host;    }    public function get($script, $params = array(), $method = self::METHOD_GET, $header = false, $http_headers = null)    {        if (is_array($params)) {            $qs = http_build_query($params);        }        else {            $qs = $params;        }        $url = rtrim($this->host, "/") . "/" . ltrim($script, "/");        $curl = curl_init();        if ($method == self::METHOD_POST) {            curl_setopt($curl, CURLOPT_POST, true);            curl_setopt($curl, CURLOPT_POSTFIELDS, $qs);        } else {            $curl = curl_init();            $url = $url . "?" . $qs;        }        curl_setopt($curl, CURLOPT_URL, $url);        curl_setopt($curl, CURLOPT_HEADER, $header);        if (!is_null($http_headers)) {            curl_setopt($curl, CURLOPT_HTTPHEADER, $http_headers);        }        curl_setopt($curl, CURLOPT_SSL_VERIFYPEER, false);        curl_setopt($curl, CURLOPT_SSL_VERIFYHOST, false);        curl_setopt($curl, CURLOPT_USERAGENT, 'Mozilla/5.0 (X11; Linux i686) AppleWebKit/537.4 (KHTML, like Gecko) Ubuntu/12.10 Chromium/22.0.1229.94 Chrome/22.0.1229.94 Safari/537.4');        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);        curl_setopt($curl, CURLOPT_CONNECTTIMEOUT, 10);        curl_setopt($curl, CURLOPT_AUTOREFERER, true);        $output = curl_exec($curl);        if (curl_error($curl)) {            throw new Exception(curl_error($curl), curl_errno($curl));        }        $this->last_response = $output;        $this->last_http_code = curl_getinfo($curl, CURLINFO_HTTP_CODE);        curl_close($curl);        return $output;    }}